import React, { useState, useEffect }  from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

const PrintData = ({country}) =>{
  return <li>{country}</li>
}


const App = () => {

  const [countries, setContries] = useState([]);
  const [newSearch, setNewSearch ] = useState();

  const handleSearchBarChange = event =>{
    setNewSearch(event.target.value);
  }

  const countryToSearch = newSearch;

  const countryToSearchToUpper = countryToSearch.charAt(0).toUpperCase() + countryToSearch.substring(1);

  const countryFilter = countries.map( country => country)

  console.log(countryFilter.filter(el => el.name.charAt(0).includes(countryToSearchToUpper)));
  useEffect(() => {
    axios
    .get('https://restcountries.eu/rest/v2/all')
    .then(response => {
      setContries(response.data)
    })
  }, [])

  return ( 
  <div>  
      <p>find countries <input 
      value={newSearch}
      onChange={handleSearchBarChange}/>
      </p>
    <div>debug: {newSearch}</div>
    <ul>
    </ul>
  </div>
  )
}


ReactDOM.render(
    <App />,
  document.getElementById('root')
);
